import React, { useState, useEffect } from 'react';
import './CaseStudies.css';

const CaseStudies = () => {
    const [currentSlide, setCurrentSlide] = useState(0);

    // Function to move to the next slide
    const nextSlide = () => {
        setCurrentSlide((prevSlide) => (prevSlide === 2 ? 0 : prevSlide + 1));
    };

    // Function to move to the previous slide
    const prevSlide = () => {
        setCurrentSlide((prevSlide) => (prevSlide === 0 ? 2 : prevSlide - 1));
    };

    useEffect(() => {
        // Set interval to automatically change the slide every 5 seconds
        const intervalId = setInterval(nextSlide, 5000);

        // Clear interval on component unmount to prevent memory leaks
        return () => clearInterval(intervalId);
    }, []); // Run once on component mount

    return (
        <div className="case-studies">
            <div className="title">
                <div className="rectangle"></div>
                <h2>Case Studies</h2>
            </div>
            <div className="case-row">
                {[0, 1, 2].map((index) => (
                    <div key={index} className={currentSlide === index ? 'case-column active' : 'case-column'}>
                        <div className="case-image-container">
                            <img src={`${process.env.PUBLIC_URL}/case-${index + 1}.jpeg`} alt={`Case Study ${index + 1}`} className="image" />
                            <div className="text-overlay">
                                <h3>Case Study {index + 1}</h3>
                                <p>Description of Case Study {index + 1}</p>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
            <button className="prev" onClick={prevSlide}>&#10094;</button>
            <button className="next" onClick={nextSlide}>&#10095;</button>
        </div>
    );
}

export default CaseStudies;
