import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Brands.css'; // Import the CSS file for styling

const Brands = () => {
    const [images, setImages] = useState([]);
    const [sortedImages, setSortedImages] = useState([]);
    const [sortOrder, setSortOrder] = useState('asc');

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = async () => {
        try {
            const response = await axios.get('http://localhost:3001/api/images');
            setImages(response.data);
            setSortedImages([...response.data]); // Clone images array for sorting
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    };

    const handleSortChange = (event) => {
        const newSortOrder = event.target.value;
        setSortOrder(newSortOrder);

        // Sort images based on sortOrder
        const sorted = [...sortedImages].sort((a, b) => {
            if (newSortOrder === 'asc') {
                return a.name.localeCompare(b.name);
            } else {
                return b.name.localeCompare(a.name);
            }
        });
        setSortedImages(sorted);
    };

    const decodeBase64 = (base64String) => {
        return atob(base64String);
    };

    return (
        <div className="brands-container">
            <div className="title">
                <div className="rectangle"></div>
                <h2>You'll be in good company</h2>
            </div>
            <h2 className="section-description">Trusted by our leading brands</h2>

            <div className="sort-dropdown">
                <label htmlFor="sort">Sort by name:</label>
                <select id="sort" value={sortOrder} onChange={handleSortChange}>
                    <option value="asc">Ascending</option>
                    <option value="desc">Descending</option>
                </select>
            </div>
            <div className="brands-row">
                {sortedImages.map((image, index) => (
                    <div key={index} className="brand-wrapper">
                        <svg
                            xmlns="http://www.w3.org/2000/svg"
                            dangerouslySetInnerHTML={{ __html: decodeBase64(image.data) }}
                            className="brand-image"
                        ></svg>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Brands;
